import React,{useState} from 'react';
import { ReactComponent as Delete } from '../assets/svg/delete.svg'



const Component = ({ component, onDeleteComponent, onAddSection, onDeleteSection }) => {
  return (
    <div style={{ backgroundColor: 'pink', height: 360, width: 360 }}>
      <div style={{ marginTop: 10, alignSelf: 'flex-end', paddingRight: 10 }}>
        <button onClick={onAddSection}>Add Section</button>
        <button onClick={onDeleteComponent}>Delete Component</button>
      </div>
      <div style={{ textAlign: 'center' }}>Component {component.index}</div>
      {component.sections.map((_, sectionIndex) => (
        <div key={sectionIndex} style={{ marginTop: 10, padding: 5, border: '1px solid #ddd' }}>
          Section {sectionIndex + 1}
          <button onClick={() => onDeleteSection(sectionIndex)}>Delete Section</button>
        </div>
      ))}
    </div>
  );
};

function HomePage() {
    const [components, setComponents] = useState<any>([{}]);

    const addComponent = () => {
      setComponents([...components, {}]);
    };


    const addSection = (componentIndex: number) => {
        const updatedComponents = [...components];
        updatedComponents[componentIndex].sections = updatedComponents[componentIndex].sections || [];
        updatedComponents[componentIndex].sections.push({});
        setComponents(updatedComponents);
      };


      const deleteComponent = (componentIndex: number) => {
        const updatedComponents = [...components];
        updatedComponents.splice(componentIndex, 1);
        setComponents(updatedComponents);
      };

      const deleteSection = (componentIndex: number, sectionIndex: number) => {
        const updatedComponents = [...components];
        updatedComponents[componentIndex].sections.splice(sectionIndex, 1);
        setComponents(updatedComponents);
      };

    return (
      <div style={{ backgroundColor: '#a4a4a4', flex: 1 }}>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(360px, 1fr))', gap: '20px' }}>
        {components.map((component: any, componentIndex: number) => (
          <div key={componentIndex} style={{ backgroundColor: 'pink', height: 360, width: 360 }}>
            <div style={{ marginTop: 10, alignSelf: 'flex-end', paddingRight: 10 }}>
              <button onClick={() => addComponent()}>Add another component</button>
              <button onClick={() => addSection(componentIndex)}>Add Section</button>
              <button onClick={() => deleteComponent(componentIndex)}>Delete Component</button>
            </div>
            <div style={{ textAlign: 'center' }}>Component {componentIndex + 1}</div>
            {component.sections && component.sections.map((_:any, sectionIndex: number) => (
              <div key={sectionIndex} style={{ marginTop: 10, padding: 5, border: '1px solid #ddd' }}>
                Section {sectionIndex + 1}
                {/* <button onClick={() => deleteSection(componentIndex, sectionIndex)}>Delete Section</button> */}
                <Delete onClick={() => deleteSection(componentIndex, sectionIndex)}/>
              </div>
            ))}
          </div>
        ))}
      </div>
      </div>
    );
}

export default HomePage;
